def create_default_buyersguide_categories(apps, schema_editor):
    BuyersGuideProductCategory = apps.get_model("wagtailpages", "BuyersGuideProductCategory")
    categories = [
        "Toys & Games",
        "Smart Home",
        "Entertainment",
        "Wearables",
        "Health & Exercise",
        "Pets",
        "Valentine's Day",
        "Video Call Apps",
    ]

    for cat in categories:
        category, created = BuyersGuideProductCategory.objects.get_or_create(name=cat)
        if created:
            category.slug = slugify(cat)
            category.save()


